import { StatusBar } from 'expo-status-bar';
import { useEffect, useState } from 'react';
import {
  StyleSheet,
  Text,
  View,
  FlatList,
  SectionList,
  TouchableOpacity,
} from 'react-native';

import CustomButton from './CustomButton';
import API from './API';

export default function App() {
  const [data, setData] = useState([]);

  const DATAs = [
    {
      title: 'Fruits',
      data: ['Apple', 'Banana', 'Grape', 'Orange', 'Strawberry'],
    },
    {
      title: 'Vegetables',
      data: ['Carrot', 'Celery', 'Cucumber', 'Tomato'],
    },
    {
      title: 'Meats',
      data: ['Beef', 'Chicken', 'Pork'],
    },
  ];

  const DATA = [];
  let mensClothing = [];
  let jewelery = [];
  let electronics = [];
  let womensClothing = [];

  const APICall = async () => {
    const response = await API();

    response.map((item) => {
      if (item.category === "men's clothing") {
        mensClothing.push(item);
      } else if (item.category === 'jewelery') {
        jewelery.push(item);
      } else if (item.category === 'electronics') {
        electronics.push(item);
      } else if (item.category === "women's clothing") {
        womensClothing.push(item);
      }
    });
    // console.log('mensClothing: ', mensClothing);
    // console.log('jewelery: ', jewelery);
    // console.log('electronics: ', electronics);
    // console.log('womensClothing: ', womensClothing);

    DATA.push({ title: "Men's clothing", data: mensClothing });
    DATA.push({ title: 'Jewelery', data: jewelery });
    DATA.push({ title: 'Electronics', data: electronics });
    DATA.push({ title: "Women's clothing", data: womensClothing });

    // DATA = [
    //   { title: 'Jewelery', data: jewelery },
    //   { title: 'Electronics', data: electronics },
    //   { title: "Women's clothing", data: womensClothing },
    // ];

    setData(DATA);
    console.log('data: ', data);
    console.log('DATA: ', DATA);
    console.log('DATAs: ', DATAs);
  };

  useEffect(() => {
    APICall();
  }, []);

  const Item = ({ title }) => (
    <View style={styles.item}>
      <Text style={styles.title}>{title}</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      <SectionList
        sections={DATAs}
        keyExtractor={(item, index) => item + index}
        renderItem={({ item }) => (
          <View style={styles.item}>
            <Text style={styles.title}>{item}</Text>
          </View>
        )}
        renderSectionHeader={({ section: { title } }) => (
          <Text style={styles.header}>{title}</Text>
        )}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: StatusBar.currentHeight,
    marginHorizontal: 16,
  },
  item: {
    backgroundColor: '#f9c2ff',
    padding: 20,
    marginVertical: 8,
  },
  header: {
    fontSize: 32,
    backgroundColor: '#fff',
  },
  title: {
    fontSize: 24,
  },
});
